import "core-js/modules/es.array.push.js";
import { fetchContacts, saveContacts } from '@/utils/storage';
export default {
  props: ['id'],
  data() {
    return {
      contact: null // Will hold the contact to update
    };
  },
  mounted() {
    this.loadContact();
  },
  methods: {
    loadContact() {
      const contacts = fetchContacts();
      this.contact = contacts.find(contact => contact.id.toString() === this.id);
      // Initialize form fields with this.contact's details
    },
    submitUpdate() {
      // Assuming `contacts` is an array of all contacts and `this.contact` is the updated contact
      const contacts = fetchContacts();
      const index = contacts.findIndex(c => c.id === this.contact.id);
      if (index !== -1) {
        contacts[index] = this.contact; // Update the contact in the array
        saveContacts(contacts); // Persist the updated array to storage
        this.$router.push({
          name: 'ContactDetails',
          params: {
            id: this.contact.id
          }
        }); // Navigate to the updated contact's details view
      } else {
        console.error('Contact not found');
        // Handle the error case
      }
    }
  }
};
